CREATE TABLE GPU (
    `list_name` VARCHAR(40) CHARACTER SET utf8,
    `list_series` VARCHAR(26) CHARACTER SET utf8,
    `list_chipset` VARCHAR(20) CHARACTER SET utf8,
    `list_memory` VARCHAR(4) CHARACTER SET utf8,
    `list_coreclock` VARCHAR(7) CHARACTER SET utf8,
    `list_price` NUMERIC(6, 2)
);
